import CountryList from './components/CountryList';
import {Component } from 'react';
import CountryService from './services/CountryService';
import SearchComponent from './components/SearchComponent';
export default class App extends Component {

  state={metin:"",countries:[]}
  changeCountries=capitalName=>{
    CountryService.getCountriesByCapitalName(capitalName).then((response)=>{
      this.setState({countries:response.data,metin:capitalName})
    });
  }
  yenifonksiyon=yazi=>{
    this.setState({metin:yazi})
  }


  render(){
    return (
      <div className="container">
        <SearchComponent yenifonksiyon={this.yenifonksiyon} changeCountries={this.changeCountries}/>
        <CountryList deneme={this.state.deneme} countries={this.state.countries}/>
      </div>
    );
  }

}
