import userEvent from '@testing-library/user-event';
import React from 'react';
import CountryService from '../services/CountryService';
import SearchComponent from './SearchComponent';
class CountryList extends React.Component{


    constructor(props){
        super(props)
        this.state  = {
            countries:[]
        }
    }

    changeCountries= response=>{
        this.setState({countries:response.data})
    }

    componentDidMount(){
        CountryService.getCountriesByCapitalName().then((response)=>{
            this.changeCountries(response);
        });
    }

    render(){
        return (
            <div className="container">
                <h1 className="text-center ">Country List</h1>
                <SearchComponent/>
                <table className="table table-striped">
                    <thead>
                        <th>Name</th>
                        <th>Capital</th>
                        <th>Region</th>
                        <th>Flag</th>
                    </thead> 
                    <tbody>
                        {
                            this.state.countries.map(
                                country=>
                                <tr>
                                    <td>{country.name}</td>
                                    <td>{country.capital}</td>
                                    <td>{country.region}</td>
                                    <td><img alt={country.name} width="40" height="40" src={country.flag}></img></td>
                                </tr>
                            )
                        }
                    </tbody>

                </table>

            </div>
        );
    }
}

export default CountryList;
