import React, { Component } from 'react'

export default class Search extends Component {

    componentDidMount(){
        this.props.searchByCapital("");
    }

    render() {

        return (
            <div>
                <div className="row">
                    <div className="col-6">
                        <label htmlFor="searchAll" className="col-md-1">Search For Any:</label>
                        <input id="searchAll" onChange={()=>this.searchForAll()} className="form-control col-6 col-md-8" type="text"/>
                    </div>
                    <div className="col-6">
                        <label htmlFor="searchForCapital" className="col-3 col-md-1">Search:</label>
                        <input id="searchForCapital" onChange={()=>this.searchForCapital()} className="form-control col-6 col-md-8" type="text"/>
                    </div>
                </div>
            </div>
        )
    }

    searchForCapital(){
        const text = document.getElementById('searchForCapital').value;
        this.props.searchByCapital(text);
    }

    searchForAll(){
        const text = document.getElementById('searchAll').value;
        this.props.searchAllColumns(text);
    }


}
