import React, { Component } from 'react'

export default class Search extends Component {

    componentDidMount(){
        this.props.changeCountries("");
    }

    render() {

        return (
            <div className="row offset-md-3">
                <label htmlFor="search" className="col-md-1">Search:</label>
                <input id="search" onChange={()=>this.update()} className="form-control w-50 col-md-8" type="text"/>
            </div>
        )
    }

    update(){
        const text = document.getElementById('search').value;
        this.props.changeCountries(text);
    }
}
